var autonomy = exports;
var ardrone = require('ar-drone');

exports.EKF = require('./lib/EKF');
exports.Camera = require('./lib/Camera');
exports.Controller = require('./lib/nyController');
exports.Mission = require('./lib/Mission');

exports.createControl = function(client, options) {
	var client = client || ardrone.createClient(options);
    return new autonomy.Controller(client, options);
}

exports.createMission = function(client, controller, options) {
    var client  = client || ardrone.createClient(options);
    var control = controller || autonomy.createControl(client, options);
    return new autonomy.Mission(client, control, options);
}

